public class CubePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method 
		int a=4;
		for(int i=1;i<=a;i++) 
		{
			for(int j=1;j<a;j++) {
			System.out.print("* ");
			}
			System.out.println("* ");
		}

	}

}
